import React, { Component } from "react";
import axios from "axios";
import "../SalesManagement/SalesManagement.css";
class Upload extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      description: "",
      size: "",
      color: "",
      type: "",
      shape: "",
      gender: "",
      lenstype: "",
      mainImageLink: "",
      subImgLink1: "",
      subImgLink2: "",
      subImgLink3: "",
      price: "",
      discount: "",
      brand: "",
    };
    this.changeFname = this.changeFname.bind(this);
    this.changeLname = this.changeLname.bind(this);
    this.changeemail = this.changeemail.bind(this);
    this.changemob = this.changemob.bind(this);
    this.changeitemspurchased = this.changeitemspurchased.bind(this);
    this.changepaymentmode = this.changepaymentmode.bind(this);
    this.ItemNo = this.ItemNo.bind(this);
    this.changeSubmit = this.changeSubmit.bind(this);
  }
  changeFname(event) {
    this.setState({
      Fname: event.target.value,
    });
  }
  changeLname(event) {
    this.setState({
      Lname: event.target.value,
    });
  }
  changemob(event) {
    this.setState({
      mob: event.target.value,
    });
  }
  changeemail(event) {
    this.setState({
      email: event.target.value.toString(),
    });
  }

  changeitemspurchased(event) {
    this.setState({
      itemspurchased: event.target.value,
    });
  }
  changepaymentmode(event) {
    this.setState({
      paymentmode: event.target.value,
    });
  }

  ItemNo(event) {
    this.setState({
      ItemNo: event.target.value,
    });
  }
  changeSubmit(event) {
    console.log(event);
    axios
      .post("http://localhost:7000/SalesBook", {
        mob: this.state.mob,
        Fname: this.state.Fname,
        Lname: this.state.Lname,
        email: this.state.email,
        itemspurchased: this.state.itemspurchased,
        paymentmode: this.state.paymentmode,
        ItemNo: this.state.ItemNo,
      })
      .then((res) => {
        console.log(res);
        alert("Sales data updated ");
      })
      .catch((err) => {
        console.log(err);
      });
  }

  render() {
    return (
      <div>
        <div className="sales-testbox">
          <form className="sales-form" onSubmit={this.changeSubmit}>
            <div className="sales-banner">
              <h1 className="sales-title">Upload Item</h1>
            </div>
            <fieldset className="sales-item">
              <legend>Title</legend>
              <div className="sales-item-name">
                <input
                  type="text"
                  name="title"
                  placeholder="Title"
                  value={this.state.Lname}
                  onChange={this.changeLname}
                />
              </div>
            </fieldset>
            <fieldset className="sales-item">
              <legend>Description</legend>
              <input
                type="text"
                name="description"
                value={this.state.email}
                onChange={this.changeemail}
              />
            </fieldset>
            <fieldset className="sales-item">
              <legend>Size</legend>
              <input
                type="number"
                name="size"
                placeholder="Size"
                value={this.state.mob}
                onChange={this.changemob}
              />
            </fieldset>
            <fieldset className="sales-item">
              <legend>Color</legend>
              <input
                type="text"
                name="color"
                placeholder="Color"
                value={this.state.mob}
                onChange={this.changemob}
              />
            </fieldset>
            <fieldset className="sales-item">
              <legend>Shape</legend>
              <input
                type="text"
                name="shape"
                placeholder="Shape"
                value={this.state.mob}
                onChange={this.changemob}
              />
            </fieldset>
            <fieldset className="sales-item">
              <legend>Type</legend>
              <input
                type="text"
                name="type"
                placeholder="Type"
                value={this.state.mob}
                onChange={this.changemob}
              />
            </fieldset>
            <fieldset className="sales-item">
              <legend>Gender</legend>
              <input
                type="text"
                name="gender"
                placeholder="Gender"
                value={this.state.mob}
                onChange={this.changemob}
              />
            </fieldset>
            <fieldset className="sales-item">
              <legend>Lens Type</legend>
              <input
                type="text"
                name="lenstype"
                placeholder="Lens Type"
                value={this.state.mob}
                onChange={this.changemob}
              />
            </fieldset>
            <fieldset className="sales-item">
              <legend>Main Display Image Link</legend>
              <input
                type="text"
                name="mainImageLink"
                placeholder="Main Display Image Link"
                value={this.state.mob}
                onChange={this.changemob}
              />
            </fieldset>
            <fieldset className="sales-item">
              <legend>Sub Image link 1</legend>
              <input
                type="text"
                name="subImgLink1"
                placeholder="Sub Image link 1"
                value={this.state.mob}
                onChange={this.changemob}
              />
            </fieldset>
            <fieldset className="sales-item">
              <legend>Sub Image link 2</legend>
              <input
                type="text"
                name="subImgLink2"
                placeholder="Sub Image link 2"
                value={this.state.mob}
                onChange={this.changemob}
              />
            </fieldset>

            <fieldset className="sales-item">
              <legend>Sub Image link 3</legend>
              <input
                type="text"
                name="subImgLink3"
                placeholder="Sub Image link 3"
                value={this.state.mob}
                onChange={this.changemob}
              />
            </fieldset>
            <fieldset className="sales-item">
              <legend>Price</legend>
              <input
                type="number"
                name="price"
                placeholder="Price"
                value={this.state.mob}
                onChange={this.changemob}
              />
            </fieldset>
            <fieldset className="sales-item">
              <legend>Discount</legend>
              <input
                type="number"
                name="discount"
                placeholder="Discount"
                value={this.state.mob}
                onChange={this.changemob}
              />
            </fieldset>
            <fieldset className="sales-item">
              <legend>Brand</legend>
              <input
                type="text"
                name="brand"
                placeholder="Brand"
                value={this.state.mob}
                onChange={this.changemob}
              />
            </fieldset>
            <fieldset className="Item No">
              <legend>Item Number</legend>
              <textarea
                type="number"
                name="itemNo"
                placeholder="Item Number"
                value={this.state.ItemNo}
                onChange={this.ItemNo}
              ></textarea>
            </fieldset>
            <div className="sales-btn-div">
              <button type="submit" className="sales-btn">
                Submit
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}
export default Upload;
